function sorter(a, b) {
  var x = a[0];
  var y = b[0];
 
  return (x < y) ? -1 : 1;
  
}

var proj = proj_pre.sort(sorter);

async function load_home() {
  console.log("hi");
  if (screen.availWidth < 1200){
    document.getElementById("bod").innerHTML = `<a style="color:aqua;font-size:2em;left:10%;position:absolute;top:30%;">The Venom Wall application is currently only available for PC</a>`;
    return;
  }
}
window.load_home = load_home;

async function load_code(){
  let wd = screen.availWidth;
  console.log(wd);
  var wf = 1530/wd;
  console.log(wf);
  var new_width = "";
  if (wd < 1200){
    document.getElementById("bod").innerHTML = `<a style="color:aqua;font-size:2em;left:10%;position:absolute;top:30%;">The Venom Wall application is currently only available for PC</a>`;
    return;
  }
  else if (wd <1500 && wd >= 1200){
    new_width = (24/wf + 4).toString();
    console.log(new_width);
    document.getElementById("ht2").style.width = new_width.concat("%");
    var new_start = (2*(1+30*(wd-1200)/1200)).toString();
    document.getElementById("descr").style.left = new_start.concat("%");  
  }
  else if (wd >= 1500 && wd < 1800){
    new_width = (20/wf).toString();
    document.getElementById("ht2").style.width = new_width.concat("%"); 
  }
  else {
    new_width = (18/wf).toString();
    document.getElementById("ht2").style.width = new_width.concat("%"); 
  }
}
window.load_code = load_code;

async function load_soon() {
  if (screen.availWidth < 1200){
    document.getElementById("bod").innerHTML = `<a style="color:aqua;font-size:2em;left:10%;position:absolute;top:30%;">The Venom Wall application is currently only available for PC</a>`;
    return;
  }
  console.log("hi");
  var url = window.location.toString();
  var queryString = url.substring(url.indexOf('?') + 1);
  var a = queryString.split("|");
  var pagetype = a[0];
  var root = document.getElementById("descr");
  if (pagetype == "jobs"){
    document.getElementById("a_jo").className = "active";
    root.innerHTML = `A Page with the latest Job listings in the Venom Ecosystem. If you are a project building in the Venom Ecosystem looking to hire, we welcome you to contact us at <a style="color:white;font-family:candara;" href="mailto:thevenomwall@gmail.com">thevenomwall@gmail.com</a> to post a listing. <br/> Coming Soon!`;
    
  }
  else if (pagetype == "investors"){
    document.getElementById("a_in").className = "active";
    root.innerHTML = `A Page with the list of Potential Investors in the Venom Ecosystem. If you are an angel or private round investor and want to be listed as one, please contact us at <a style="color:white;font-family:candara;" href="mailto:thevenomwall@gmail.com">thevenomwall@gmail.com</a> <br/>Coming Soon`;
    
  }
  else if (pagetype == "code"){
    document.getElementById("a_co").className = "active";
    root.innerHTML = `A Page with links to various libraries and repositories of Code in the Venom Ecosystem. If you are building in the Venom Ecosystem and want to share your library links, we request you to contact us at <a style="color:white;font-family:candara;" href="mailto:thevenomwall@gmail.com">thevenomwall@gmail.com</a> and we would be happy to feature you.<br/>Coming Soon`;
    
  }
  else {
    console.log("unidentified");
  }

}
window.load_soon = load_soon;

async function load_project() {
  if (screen.availWidth < 1200){
    document.getElementById("bod").innerHTML = `<a style="color:aqua;font-size:2em;left:10%;position:absolute;top:30%;">The Venom Wall application is currently only available for PC</a>`;
    return;
  }
  var url = window.location.toString();
  var queryString = url.substring(url.indexOf('?') + 1).replace("%20"," ");
  var el = document.getElementById("pname");
  el.textContent = queryString;
  var im = "";
  var ws = "";
  var tw = "";
  var ds = "";
  var txt = "";
  var feed = "";

  var i = 0;
  while (i < proj.length){
    if (queryString == proj[i][0]){
      im = proj[i][2];
      ws = proj[i][3];
      tw = proj[i][4];
      ds = proj[i][5];
      txt = proj[i][6];
      feed = tw.concat("?ref_src=twsrc%5Etfw");
      break;
    }
    i++;
  }
  document.getElementById("pimage").src = "./img/".concat(im);
  if (im == "no-results.png"){
    document.getElementById("pimage").style.border="none";
  }
  var t = txt.split("\n");
  i = 0;
  var el2 = document.getElementById("ptext");
  while (i < t.length){
    el2.innerHTML += `<br/>`.concat(t[i]);
    i++;
  }
  
  document.getElementById("pweb").href = ws;
  document.getElementById("pweb").style.color="whitesmoke";
  if (tw != "coming soon"){
    document.getElementById("ptwi").href = tw;
    document.getElementById("ptwi").style.color="whitesmoke";
    document.getElementById("mtwfeed").href = feed;
  }
  else {
    document.getElementById("ptwi").removeAttribute("href");
    document.getElementById("ptwi").style.color="red";
    document.getElementById("ptwi").style.fontWeight="100";
    document.getElementById("ptwi").style.cursor="default";
    document.getElementById("ptwfeed").style.display="none";
  }
  if (ds != "coming soon"){
    document.getElementById("pdis").href = ds;
    document.getElementById("pdis").style.color="whitesmoke";
  }
  else {
    document.getElementById("pdis").removeAttribute("href");
    document.getElementById("pdis").style.color="red";
    document.getElementById("pdis").style.fontWeight="100";
    document.getElementById("pdis").style.cursor="default";
  }

  

  
}
window.load_project = load_project;



async function load_projects_all() {
  if (screen.availWidth < 1200){
    document.getElementById("bod").innerHTML = `<a style="color:aqua;font-size:2em;left:10%;position:absolute;top:30%;">The Venom Wall application is currently only available for PC</a>`;
    return;
  }

  var factor = 5;
  if (screen.availWidth <= 1500){
    factor = 4;
  }
  
  if (factor == 4){
    document.getElementById("h_im").style.marginLeft = "15%";
    document.getElementById("h_tx").style.marginLeft = "15%";
    document.getElementById("sbtn").style.width = "130%";
  }

 
  document.getElementById("menuBar").style.height = "580px";

  document.getElementById("project_key").value = "";
  var i = 0;
  var root = document.getElementById("rootx");
  var childrenIfAny = root.childNodes;
  while (childrenIfAny.length != 0){
    var e = childrenIfAny[0];
    e.remove();
    childrenIfAny = root.childNodes;
  }
  var elr = document.createElement('div');
    elr.className = 'row cardRow';
  while (i < proj.length){
    if (i % factor == 0 && i != 0){
      root.appendChild(elr);
      var space = document.createElement('br');
      root.appendChild(space);
      space = document.createElement('br');
      root.appendChild(space);
      elr = document.createElement('div');
      elr.className = 'row cardRow';
    }
    var el = document.createElement('div');
    el.className = "col";
    el.innerHTML = `
    <div class="exploreCardBody">
                        <div class="exploreCard">
                          <img
                            class="homeImg2"
                            src="img/`.concat(proj[i][2]).concat(`"
                            alt=""
                            style="width: 300px; height: 175px; object-fit: fill;"
                           
                          />
                          <div class="exploreCardFooter" style="text-align:right;" >
                            <div class="exploreCardText">`
                              .concat("").concat(
                            `</div>
                          </div>
                        </div>
                        <div class="exploreCardButton">
                          <a id="icpvg" class="exploreCardBtn" href="browse.html?`.concat(proj[i][0]).concat(`">`.concat(proj[i][0]).concat(`</a>
                        </div>
                      </div>
    `))));
    
    elr.appendChild(el);
    i += 1;
  }
  while (i % factor != 0){
    var el = document.createElement('div');
    el.className = "col";
    elr.appendChild(el);
    i += 1;
  }

  root.appendChild(elr);

}
window.load_projects_all = load_projects_all;


